                        {/*        ? (<div><button onClick={this.btnHandler}>GET INFORMATION</button></div>)*/}
                        {/*        : (<div><Class /></div>)*/}
                        {/*}*/}

                    </div>
                </UserContext.Provider>

        );
    }
}

