import React, { Component } from 'react';


export class ClassComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date()};
    }

    componentDidMount() {
        this.timerID = setInterval(() => this.time(), 1000);
    }

    componentWillUnmount() {
        clearInterval(this.timerID);
    }

    time() {
        this.setState({
            date: new Date()
        });
    }

    // start() {
