    // }
    //
    // stop() {
    //
    // }

    render() {
        return (
            <div>
                <h1>Clocks</h1>
                <h2>{this.state.date.toLocaleTimeString()}</h2>
                <button>START</button>
                <button>STOP</button>
            </div>
        );
    }
}

export default ClassComponent;
